DROP TABLE IF EXISTS `pt_operator`;
CREATE TABLE `pt_operator` (
  `ids` varchar(32) NOT NULL,
  `version` bigint(20) DEFAULT NULL COMMENT '版本号',
  `description` varchar(200) DEFAULT NULL,
  `names` varchar(25) DEFAULT NULL COMMENT '操作名称',
  `onemany` char(1) DEFAULT NULL,
  `returnparamkeys` varchar(100) DEFAULT NULL,
  `returnurl` varchar(200) DEFAULT NULL,
  `rowfilter` char(1) DEFAULT NULL,
  `url` varchar(200) DEFAULT NULL COMMENT '请求url',
  `moduleids` varchar(32) DEFAULT NULL COMMENT '模块id',
  `splitpage` char(1) DEFAULT NULL COMMENT '是否分页',
  `formtoken` char(1) DEFAULT NULL COMMENT '表单重复提交验证',
  `ipblack` char(1) DEFAULT NULL,
  `privilegess` char(1) DEFAULT NULL COMMENT '验证权限？ 1：需要 0：不需要',
  `ispv` char(1) DEFAULT NULL,
  `pvtype` char(1) DEFAULT NULL,
  `modulenames` varchar(50) DEFAULT NULL COMMENT '所属模块名称',
  PRIMARY KEY (`ids`),
  KEY `onemanyindex` (`onemany`) USING BTREE,
  KEY `returnparamkeysindex` (`returnparamkeys`) USING BTREE,
  KEY `returnurlindex` (`returnurl`) USING BTREE,
  KEY `rowfilterindex` (`rowfilter`) USING BTREE,
  KEY `urlindex` (`url`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='功能';

